-- ## Connect to The DataBase
USE poc_retail_db;

-- ## Create External Table - Daily Revenue
CREATE EXTERNAL TABLE tb_ext_daily_revenue (
    event_date Date,
    daily_revenue FLOAT,
    total_purchases INT
)
WITH    (
    LOCATION = 'gold/daily_revenue/',
    DATA_SOURCE = adl_retail_datasource,
    FILE_FORMAT = ParquetFormat
)
;

select * from dbo.tb_ext_daily_revenue;

-- ## Create External Table - Week Day Revenue
CREATE EXTERNAL TABLE poc.tb_ext_week_day_revenue 
    (
        event_daya VARCHAR,
        tot_revenue FLOAT,
        tot_transactions INT
    )
WITH    
    (
        LOCATION = 'gold/week_day_revenue/',
        DATA_SOURCE = adl_retail_datasource,
        FILE_FORMAT = ParquetFormat
    )
;

select * from poc.tb_ext_week_day_revenue;
